import groovy.sql.Sql

List storefront1 = new ArrayList()
List storefront2 = new ArrayList()

def (site1, site2) = "${SitePair}".tokenize('-')
def sql = Sql.newInstance("jdbc:mysql://localhost:3306/siteinformation", "root", "Advent2016!", "com.mysql.jdbc.Driver")
def query1 = "select IPAddress, SiteName, HostName from sfdcinformation where ComponentType like 'DC%' and (SiteName like "+"'${site1}'"+" or SiteName like "+"'${site2}'"+");"

sql.eachRow (query1)
{
storefront1 << it.IPAddress+"  "+it.SiteName+"  "+it.HostName

}

def query2 = "select IPAddress, SiteName, HostName from sfdcinformation where ComponentType like 'SF%' and (SiteName like "+"'${site1}'"+" or SiteName like "+"'${site2}'"+");"
sql.eachRow (query2)
{
storefront2 << it.IPAddress+"  "+it.SiteName+"  "+it.HostName
}
storefront1.addAll(storefront2)

return storefront1

