import hudson.model.*
import groovy.sql.Sql

def taste = build.buildVariableResolver.resolve("Test")
println "$taste"
def sitelist = build.buildVariableResolver.resolve("SitePair")

List IPs = new ArrayList()
List sysname = new ArrayList()
List hatype = new ArrayList()
List component = new ArrayList()
List storefront = new ArrayList()

def (site1, site2) = "${sitelist}".tokenize('-')
def sql = Sql.newInstance("jdbc:mysql://localhost:3306/siteinformation", "root", "Advent2016!", "com.mysql.jdbc.Driver")
def query1 = "select ComponentType, IPAddress, HostName, SiteName, HAType from sfdcinformation where SiteName like "+"'${site1}'"+";"

sql.eachRow (query1)
{
  component << it.ComponentType
  IPs << it.IPAddress
  sysname << it.HostName
  hatype << it.HAType
}

def size = component.size()
for (int j=0; j<size; j++)
{
  println component[j]
  println IPs[j]
  println sysname[j]
  println hatype[j]  
}

