script.script:
import groovy.sql.Sql

List nameslist = new ArrayList()

def sql = Sql.newInstance("jdbc:mysql://localhost:3306/siteinformation", "root", "Advent2016!", "com.mysql.jdbc.Driver")
sql.eachRow ("select distinct SitePairName from sitepair;")
{
nameslist << it.SitePairName
}
return nameslist

import groovy.sql.Sql

List storefront1 = new ArrayList()
List storefront2 = new ArrayList()

def (site1, site2) = "${SitePair}".tokenize('-')
def sql = Sql.newInstance("jdbc:mysql://localhost:3306/siteinformation", "root", "Advent2016!", "com.mysql.jdbc.Driver")
def query1 = "select IPAddress, SiteName, HostName from sfdcinformation where ComponentType like 'DC%' and (SiteName like "+"'${site1}'"+" or SiteName like "+"'${site2}'"+");"

sql.eachRow (query1)
{
storefront1 << it.IPAddress+"  "+it.SiteName+"  "+it.HostName

}

def query2 = "select IPAddress, SiteName, HostName from sfdcinformation where ComponentType like 'SF%' and (SiteName like "+"'${site1}'"+" or SiteName like "+"'${site2}'"+");"
sql.eachRow (query2)
{
storefront2 << it.IPAddress+"  "+it.SiteName+"  "+it.HostName
}
storefront1.addAll(storefront2)

return storefront1

scriptSource.command:
import groovy.sql.Sql

def taste = build.buildVariableResolver.resolve("Test")
println "$taste"
def sitelist = build.buildVariableResolver.resolve("SitePair")

List site1IPs = new ArrayList()
List site1sysname = new ArrayList()
List site1hatype = new ArrayList()
List site1component = new ArrayList()
List site1storefront = new ArrayList()

def (site1, site2) = "${sitelist}".tokenize('-')
def sql = Sql.newInstance("jdbc:mysql://localhost:3306/siteinformation", "root", "Advent2016!", "com.mysql.jdbc.Driver")
def query1 = "select ComponentType, IPAddress, HostName, SiteName, HAType from sfdcinformation where SiteName like "+"'${site1}'"+";"

sql.eachRow (query1)
{
  site1component << it.ComponentType
  site1IPs << it.IPAddress
  site1sysname << it.HostName
  site1hatype << it.HAType
}

def size = site1component.size()
for (int j=0; j<size; j++)
{
  println site1component[j]
  println site1IPs[j]
  println site1sysname[j]
  println site1hatype[j]  
}
for (int i=0; i<size; i++)
{
  if(site1hatype[i].equalsIgnoreCase("primary"))
  {
    if(site1component[i].equalsIgnoreCase("SF-01"))
    {
      def Site1SFServer1 = site1sysname[i]
    }
    else if(site1component[i].equalsIgnoreCase("DC-01"))
    {
      def Site1DCServer1 = site1sysname[i]
    }    
  }
  else if(site1hatype[i].equalsIgnoreCase("Secondary"))
  {
     if(site1component[i].equalsIgnoreCase("SF-02"))
    {
      def Site1SFServer2 = site1sysname[i]
    }
    else if(site1component[i].equalsIgnoreCase("DC-02"))
    {
      def Site1DCServer2 = site1sysname[i]
    }    
  }
}



